class Solution {

        int minSeq=Integer.MAX_VALUE;
    public int ladderLength(String beginWord, String endWord, List<String> wordList) {

        if (!wordList.contains(endWord)) return 0;
        List<String> sequence=new ArrayList<>();
        sequence.add(beginWord);
        travel(beginWord,endWord,wordList,sequence);
        if (minSeq==Integer.MAX_VALUE) minSeq=0;
        return minSeq;
    }

    //bfs ç¬¬ä¸€æ¬¡å°±ç®—æˆåŠŸ



    //é€’å½’éå† éœ€è¦å‰ªæ”¯
        public void travel(String currentWord,String endWord, List<String> wordList,List<String> sequence){

        if (checkWordDiffBySingleChar(currentWord,endWord)) {
            System.out.println(Arrays.toString(sequence.toArray()));
            minSeq=Math.min(minSeq,sequence.size()+1);
        }
        for (String word:wordList){
            if (!sequence.contains(word)  && checkWordDiffBySingleChar(currentWord,word)){
             //   System.out.println(word);
                sequence.add(word);
             //   if ( sequence.size()<minSeq)
                travel(word,endWord,wordList,sequence);
                sequence.remove(word);
            }
        }
        }

    //è®¡ç®—å•è¯åªç›¸å·®ä¸€ä¸ªå­—ç¬¦
        private  boolean checkWordDiffBySingleChar(String word1,String word2){
        if (word1.length()!=word2.length()) return false;
        int difCount=0;
        for (int i=0;i<word1.length();i++){
            if (word1.charAt(i)!=word2.charAt(i))
                difCount++;
        }
        return difCount==1;
        }

}
